     and xc_rank= #{xcRank,jdbcType=VARCHAR}


     and rank= #{rank,jdbcType=INTEGER}


     and educationLevel= #{educationlevel,jdbcType=INTEGER}


     and isProportion= #{isproportion,jdbcType=BIT}


     and isRange= #{isrange,jdbcType=BIT}


     and rankTrend= #{ranktrend,jdbcType=VARCHAR}


     and createDate= #{createdate,jdbcType=BIGINT}


     and creator= #{creator,jdbcType=BIGINT}


     and lastModDate= #{lastmoddate,jdbcType=BIGINT}


     and lastModifier= #{lastmodifier,jdbcType=BIGINT}


     and is_delete= #{isDelete,jdbcType=BIT}



    order by  ${order}

